DROP TABLE IDRemapper;


UPDATE UnitPromotions SET PromotionPrereqOr1 = 'PROMOTION_INFANTRY', PromotionPrereqOr2 = 'PROMOTION_UNMOUNTED_ARCHER', PromotionPrereqOr3 = 'PROMOTION_HORSE_MOUNTED', PromotionPrereqOr4 = 'PROMOTION_ELEPHANT', PromotionPrereqOr5 = 'PROMOTION_ANIMAL', PromotionPrereqOr6 = 'PROMOTION_WOODEN', PromotionPrereqOr7 = 'PROMOTION_METAL' WHERE Type = 'PROMOTION_COMBAT_1';
